// yellow highlight on h3 background when hovered
$(function(){$(".folder-abt, .folder-drs, .folder-loc, .folder-nws, .folder-dia, .folder-log, .folder-egl").hover(function(){var e=$(this).index(),t=$("h3").get(e);$(t).addClass("h3-hover")},function(){$(".h3-hover").removeClass("h3-hover")})});$(".folder-group a").each(function(e){e!=0&&$("#folder-out").clone().attr("id","folder-out"+e).appendTo($(this).parent());$(this).data("folder-out",e)}).mouseover(function(){$("#folder-out"+$(this).data("folder-out"))[0].play()});$("#folder-out").attr("id","folder-out0");$(".folder-group a").each(function(e){e!=0&&$("#folder-in").clone().attr("id","folder-in"+e).appendTo($(this).parent());$(this).data("folder-in",e)}).mouseout(function(){$("#folder-in"+$(this).data("folder-in"))[0].play()});$("#folder-in").attr("id","folder-in0");$(".folder-group a").each(function(e){e!=0&&$("#folder-in").clone().attr("id","folder-in"+e).appendTo($(this).parent());$(this).data("folder-in",e)}).click(function(){$("#folder-in"+$(this).data("folder-in"))[0].play()});$("#folder-in").attr("id","folder-in0");$(document).ready(function(){$("body").fadeIn(1e3);$(".folder-group a").click(function(e){function t(){window.location=newLocation}e.preventDefault(e);newLocation=this.href;that=$(this);$(this).parent().animate({left:"300px"},200);setTimeout(function(){that.parent().addClass("zoom");$(".left-folder").animate({opacity:0},1e3)},1e3);setTimeout(function(){$("body").fadeOut(500,t)},2e3)})});